import 'package:flutter/material.dart';
import 'package:nisha/cardScrolling.dart';

void main(){
  runApp(MyApp(

  ));
}
class MyApp extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      home: Scaffold(
        appBar: AppBar(
            title: Text('Scrollable Cards')
        ),
        body: ScrollablecardList(),
      ),
    );
  }
}

class ScrollablecardList extends StatelessWidget{
  @override

  Widget build(BuildContext Context){
    return ListView(
      children: [
        SizedBox(
          width: 300,
          height: 200,
          child: ListView.builder(
              scrollDirection: Axis.horizontal,
              itemCount: 10,
              itemBuilder: (context, index){
                return Card(
                  borderOnForeground: true,
                  color: Colors.red,
                  margin: EdgeInsets.all(10),
                  elevation: 5,
                  child: Padding(
                    padding: EdgeInsets.all(20),
                    child: Row(
                      crossAxisAlignment: CrossAxisAlignment.start,
                      children: [
                        CircleAvatar(
                          child: Image.network('data:image/jpeg;base64,'),
                          radius: 50,
                        ),
                        Text('card title ${index + 1} ',
                          style: TextStyle(
                              fontSize: 18,
                              fontWeight: FontWeight.bold
                          ),
                        ),
                        SizedBox(height: 10),
                        Text('This is a description of card number '
                            '${index + 1}',
                        style: TextStyle(fontSize: 14),
                        )
                      ],
                    ),
                  ),
                );
              }
          ),
        ),
        SizedBox(
          width:400 ,
        height: 500,
          child: ListView.builder(
              scrollDirection: Axis.vertical,
              itemCount: 10,
              itemBuilder: (context, index){
              return Card(
                  borderOnForeground: true,
                  color: Colors.red,
                  margin: EdgeInsets.all(10),
                  elevation: 5,
                  child: Padding(
                  padding: EdgeInsets.all(20),
                  child: Row(
                  crossAxisAlignment: CrossAxisAlignment.start,
                  children: [
                    CircleAvatar(
                      child: Image.network('data:image/jpeg;base64,'),
                      radius: 50,
                      backgroundColor: Colors.black,
                    ),
                  Text('card title ${index + 1} ',
                  style: TextStyle(
                  fontSize: 18,
                  fontWeight: FontWeight.bold
                ),
              ),
                  ],
                  ),
              ),
                );
              }
              ),
        )
      ],
    );
  }
}